const handleImgLink = (link) => {
  if (link?.includes(`images`)) {
    // link = "http://localhost:5000/" + link;
    link = "https://ecommerce-server-frank-7ce37948d2b5.herokuapp.com/" + link;
  }

  return link;
};

export default handleImgLink;
